/**
 * Escapes a regex.
 * @param text
 */

function escapeRegex(text) {
  return text.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, "\\$&");
}
/**
 * Returns a placeholder regex.
 * @param text
 */

function placeholderRegexCallback(text) {
  return ({
    config
  }) => {
    const {
      placeholder
    } = config;
    return new RegExp(`${escapeRegex(placeholder[0])}\\s*(${text})\\s*${escapeRegex(placeholder[1])}`, "gm");
  };
}
